dict.string:
# this is just proof-of-concept, not very useful. Shows all
# methods in the current buffer as an alphabetized list of links.
cat <<EOF
<html><head>
<style>
a, a:visited {
   color: #998;
   text-decoration: none;
}
a:hover {
   color: #000;
   text-decoration: none;
}
</style></head><body><tt>
EOF
egrep -n '^(-|+)[ \t]*\(.*\)[^;]*$' \
| perl -pe 's/\(.*?\)//' \
| perl -pe 's/(\w+:)[ \t]*\(.*?\)[ \t]*\w+[ \t]*/$1/g' \
| sort -ft: -k2 \
| perl -pe 's/^(\d+):(.*?)(\{.*)?$/<a href="txmt:\/\/open?line=$1" onClick="window.close()">$2<\/a><br>/'
